package week1.day2;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -100;
		if(a>0) {
			System.out.println(a+" is a Positive number");
		}
		else {
			System.out.println(a+" is a Negative number");
		}
	}

}
